mapper.select:
select nvl(count(*),0) from alarm

	userId = #{userId}
	
		AND 
	
	
		AND cnum = #{cnum}

select alarmNum, a.userId, content, sender, TO_CHAR(senderTime, 'YYYY-MM-DD HH24:MI:SS') senderTime, NVL2(timeRead, TO_CHAR(timeRead, 'YYYY-MM-DD HH24:MI:SS'), null) timeRead, ac.cnum, name, m.nickName from alarm a join alarmCategory ac on a.cnum = ac.cnum join member m on a.sender = m.userId
a.userId = #{userId}
AND
AND a.cnum = #{cnum}
order by alarmNum desc

select alarmNum, userId, content, sender, senderTime, timeRead
from alarm
where alarmNum = #{alarmNum}
