mapper.select:
SELECT categoryNum, categoryName,use
FROM talentCategory
WHERE categoryNum = #{categoryNum}

SELECT typeNum,type
FROM tmPurchaseType
WHERE typeNum = #{typeNum}

SELECT NVL(COUNT(*),0) 
FROM talentMarket t

	
		t.categoryNum = #{categoryNum}
	
	
		AND t.typeNum = #{typeNum}
	
	
		AND

SELECT fileNum , tboardNum, fileName
FROM tmFile
WHERE tboardNum = #{tboardNum}
