mapper.select:
SELECT categoryNum, categoryName,use
FROM talentCategory
WHERE categoryNum = #{categoryNum}

SELECT typeNum,type
FROM tmPurchaseType
WHERE typeNum = #{typeNum}

		SELECT NVL(COUNT(*),0) 
		FROM talentMarket t
		
			  
                t.town = #{town}
            
			
				AND t.categoryNum = #{categoryNum}
			
			
				AND t.talentShow = #{talentShow}
			
			
				AND

SELECT t.tboardNum,t.subject,t.content,t.price,t.guidelines,t.regDate,t.userId,t.talentShow,t.optionCount,t.hitCount,t.typeNum,t.categoryNum,t.thumbnail,m.nickName, TRUNC(SYSDATE - t.regDate) AS daysDifference,town,p.type,c.categoryName FROM talentMarket t JOIN member m ON t.userId = m.userId JOIN tmpurchaseType p ON t.typeNum = p.typeNum JOIN talentCategory c ON t.categoryNum = c.categoryNum
t.town = #{town}
AND t.categoryNum = #{categoryNum}
AND t.talentShow = #{talentShow}
AND
ORDER BY tboardNum DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT fileNum , tboardNum, fileName
FROM tmFile
WHERE tboardNum = #{tboardNum}
