mapper.select:
SELECT  NVL(COUNT(*),0)
FROM togetherList t
JOIN member m ON t.userId  = m.userId


			AND 


			AND categoryNum = #{categoryNum}


	        AND town = #{town}

SELECT tNum, l.userId, m.nickName, t.categoryNum, t.categoryName, l.name, l.subject, l.memberCnt, l.thumbnail , l.town, TO_CHAR(l.regDate, 'YYYY-MM-DD') regDate, l.hitCount FROM togetherList l JOIN member m ON l.userId = m.userId JOIN togetherCategory t ON l.categoryNum = t.categoryNum
AND l.categoryNum = #{categoryNum}
AND town = #{town}
ORDER BY tNum DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

mapper.update:
UPDATE togetherList SET categoryNum=#{categoryNum}, subject=#{subject}, content=#{content}, town=#{town}, name=#{name}, 
	memberCnt=#{memberCnt}, thumbnail=#{thumbnail,jdbcType=VARCHAR}
WHERE tNum = #{tNum}
